components/wbcomponents/common/breadcrum:
{{#unless theme_settings.hide_breadcrumbs }}
<div class="wb-breadcrumbs wb-breadcrumbs-bg" itemscope itemtype="https://schema.org/BreadcrumbList">
	<div class="container">
		<div class="entry-header">
			<h3 class="entry-title">
				{{#each breadcrumbs}}
				{{#if @last}}
				{{name}}
				{{/if}}
				{{/each}}
			</h3>
			<ul class="breadcrumb" itemscope itemtype="http://schema.org/BreadcrumbList">
				{{#each breadcrumbs}}
				<li class="breadcrumb-item {{#if @last}}is-active{{/if}}" itemprop="itemListElement" itemscope itemtype="https://schema.org/ListItem">
					<a class="breadcrumb-label"
					   itemprop="item"
					   href="{{url}}"
					   {{#if @last}}aria-current="page"{{/if}}
					>
					<span itemprop="name">{{name}}</span>
					</a>
					<meta itemprop="position" content="{{add @index 1}}" />
					{{#unless @last}}<svg width="16" height="16"  class="icon"><use xlink:href="#icon-chevron-right"></use></svg> {{/unless}}

				</li>

				{{/each}}
			</ul>

		</div>
	</div>
</div>
{{/unless}}
