components/wbcomponents/addons/wb_deal/d:
{{!--
Arguments:
	- moduleId (number): section instance index number
	- class_sfx (string): Use for CSS class styling suffix
--}}
{{#if status }}
<div class="module-wrapper clearfix {{class_sfx}}">
{{> components/wbcomponents/addons/wb_deal/default/default_deals
    category_id=(lang (concat (concat 'wbTheme.wb_deals.category_' moduleId) '.category_id'))
    style=(concat 'instance' index)
}}

</div>
{{/if}}

{{#if category_id '!==' ' '}}

{{#each categories}}
	{{#if id '===' ../category_id}}
		
		{{#if (merge ../../this 
			category_name = name
			category_url = url)}}
		{{/if}}
		
	{{else}}
		{{#each children}}
			{{#if id '===' ../../../category_id}}
				{{#if (merge ../../../../this
					category_name = name
					category_url = url)}}
				{{/if}}
			{{/if}}
		{{/each}}
	{{/if}}
{{/each}}

	<h5 class="block-title">
		<span>{{lang 'wbTheme.wb_deals.heading' }}</span>
	</h5>
	<div class="block-content" >
		{{#if category_url}}
			<div class="wb-deals-container " data-products-by-category-tabs="{{ concat category_url '?sort='}}{{theme_settings.homepage_deal_sort}}" >
				<div class="productLoading">
					<img class="img-responsive lazyload" src="{{cdn '/assets/img/loading.svg'}}" alt="loading" width="50" height="50" >
				</div>
			</div>
		{{else}}

			<div class="alertBox alertBox--info"  style="margin:30px 0px">
				<div class="alertBox-column alertBox-icon">
					<icon glyph="ic-success" class="icon" aria-hidden="true"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"></path></svg></icon>
				</div>
				<p class="alertBox-column alertBox-message">
					<span>{{{lang 'wbTheme.wb_deals.noCategory' }}}</span>
				</p>
			</div>
		
		{{/if}}
	</div>
	
{{/if}}
