components/wbcomponents/addons/wb_catego:
{{!--
Arguments:
	- category_id (number):  is the number is category ID.
	- moduleId (number): section instance index number
	- class_sfx (string): Use for CSS class styling suffix
--}}

{{#if status }}
<div class="module-wrapper clearfix {{class_sfx}}">
	<h5 class="block-title">
		{{lang 'wbTheme.wb_category.heading'}}
	</h5>
	<div class="wb-category">
		<div class="productCarousel"
			data-slick='{
				"dots": false,
				"infinite": false,
				"mobileFirst": true,
				"slidesToShow": 1,
				"slidesToScroll": 1,
				"responsive": [
					{
						"breakpoint": 1900,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": {{totalCategory}}
						}
					},
					{
						"breakpoint": 1260,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": 5
						}
					},
					{
						"breakpoint": 991,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": 3
						}
					},
					{
						"breakpoint": 700,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": 2,
							"dots": true,
							"arrows": false

						}
					},
					{
						"breakpoint": 320,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": 2,
							"dots": true,
							"arrows": false

						}
					},
					{
						"breakpoint": 0,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": 1,
							"arrows": false
						}
					}
				]
			}'
		>
		{{#for totalCategory }}
			{{> components/wbcomponents/addons/wb_category/default/default_items
				category_id= (lang (concat (concat 'wbTheme.wb_category.category_' $index) '.category_id'))
				categories = ../categories
				theme_settings = ../theme_settings
				limit_subcategory = ../limit_subcategory
				placeholder= (lang 'wbTheme.wb_category.placeholderImage')
				viewAll=(lang 'wbTheme.wb_category.view_all')

			}}
		{{/for}}

		</div>
	</div>


</div>
{{/if}}

{{#each categories}}

	{{#if id '==' ../category_id}}

			{{#if (merge ../this
			category_name = name
			category_image = image
			category_url = url)}}
			{{/if}}
	{{else}}
		{{#each children}}
			{{#if id '===' ../../category_id}}
				{{#if (merge ../../this
					category_name = name
					category_url = url
					category_image = image
					)}}
				{{/if}}
			{{else}}
				{{#each children}}
					{{#if id '===' ../../../category_id}}
						{{#if (merge ../../../../this
							category_name = name
							category_url = url
							category_image = image
							)}}
						{{/if}}

					{{/if}}
				{{/each}}
				{{/if}}
		{{/each}}
	{{/if}}
{{/each}}


{{#if category_url}}
	<div class="wb-categories__items">
		<div class="wb-categories__heading">
			<h3>{{category_name}}</h3>
			<a class="shopnow" href="{{category_url}}" title="{{category_name}}">{{lang 'wbTheme.wb_category.view_all' }}</a>
		</div>
		<div class="wb-categories__thumb card-figure">
			<a href="{{category_url}}" title="{{category_name}}">
				<div class="card-img-container">
					{{> components/common/responsive-img
					image=category_image
					class="card-image"
					fallback_size=theme_settings.thumb_size
					lazyload=theme_settings.lazyload_mode
					default_image=theme_settings.default_image_product
					}}
				</div>
			</a>
		</div>

	</div>


{{/if}}
