components/wbcomponents/addons/wb_supper:
{{#each categories}}
	{{#if id '===' ../category_id}}
		{{#if (merge ../this
			category_name = name
			category_image = image
			category_url = url)}}
		{{/if}}
	{{else}}
		{{#each children}}
			{{#if id '===' ../../category_id}}
				{{#if (merge ../../this
					category_name = name
					category_url = url)}}
				{{/if}}
			{{/if}}
		{{/each}}
	{{/if}}
{{/each}}

{{#if category_url}}
	<div id="wb-listing-tabs{{moduleId}}" class="wb-listing-tabs">
		<h3 class="block-title">
			<span class="block-title--inner"  title="{{category_name}}">  {{category_name}}</span>
		</h3>
		<div class="block-content  d-flex no-gutters ">
			{{#if enableSubCategory}}
			<div class="supper-category--list col-xl-2 col-md-4 col-12">
				<ul class="blank supper--list ">
					{{#each categories}}
					{{#if id '===' ../category_id}}
							{{#each (limit children ../limit_subcategory) }}
								<li class="supper--list-item">
									<a href="{{url}}">{{name}}</a>
								</li>
							{{/each}}

						{{else}}
							{{!-- Category Level 2--}}
							{{#each children}}
								{{#if id '===' ../../category_id}}
									{{#each (limit children ../../limit_subcategory) }}
									<li class="supper--list-item ">
										<a href="{{url}}">{{name}}</a>
									</li>
									{{/each}}


								{{/if}}

							{{/each}}
						{{/if}}

					{{/each}}
					<li class="supper--list-item ltabs-tab-viewall">
						<a href="{{category_url}}" title="{{category_name}}">{{viewAll}}</a>
					</li>
				</ul>

			</div>
			{{/if}}

			{{#if enableSubCategory}}
			<div class="supper-category--container col-xl-10 col-md-8 col-12">
				{{else}}
			<div class="supper-category--container col-xl-12 col-md-12 col-12">
			{{/if}}

				<div class="ltabs-items-content"
				data-products-by-category-tabs="{{concat category_url '?sort=newest'}}"
				data-urltemplate="wbcomponents/addons/wb_supper_category/home2/default_items">
				<div class="productLoading" >
					<img class="img-responsive lazyload" src="{{cdn '/assets/img/loading.svg'}}" alt="loading" width="50" height="50" >
				</div>

			</div>


		</div>
		</div>
	</div>

	{{else}}

	<div class="alertBox alertBox--info"  style="margin:30px 0px">
		<div class="alertBox-column alertBox-icon">
			<icon glyph="ic-success" class="icon" aria-hidden="true"><svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 24 24"><path d="M12 2C6.48 2 2 6.48 2 12s4.48 10 10 10 10-4.48 10-10S17.52 2 12 2zm1 15h-2v-2h2v2zm0-4h-2V7h2v6z"></path></svg></icon>
		</div>
		<p class="alertBox-column alertBox-message">
			<span>{{{lang 'wbTheme.wb_supper_category.noCategory' }}}</span>
		</p>
	</div>
{{/if}}
