components/wbcomponents/addons/wb_banner:
{{#if status }}
<div class="module-wrapper staticBanner staticBanner--{{prefix}} {{class_sfx}}  wow fadeIn">
	{{#if (lang (concat (concat 'wbBanner.' prefix)'.heading')) '!=' ' '}}
	<h3 class="block-title">
		{{lang (concat (concat 'wbBanner.' prefix)'.heading')}}
	</h3>

	{{/if}}

	{{#if display_mode '==' 'horizontal'}}
		<div class="block-content clearfix d-flex flex-row">
	{{else if display_mode '==' 'grid121'}}
		<div class="block-content clearfix d-flex flex-row">
	{{else}}
		<div class="block-content clearfix ">
	{{/if}}

	{{#if limitation '>' 1}}
		{{#for  limitation }}
			{{#and (if ../display_mode '==' 'grid121') (if $index '==' 2)}}<div class="banners-wrapper">{{/and }}
			{{> components/wbcomponents/addons/wb_banner/default/banner-item
				theme_settings = ../theme_settings
				show_content = ../showContent
				image	= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.image'))
				placeholder	= (lang  (concat (concat (concat (concat 'wbBanner.'../prefix) '.banner') $index)'.placeholderImage'))
				icon	= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.icon'))
				url		= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.url'))
				title	= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.title'))
				text	= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.text'))
				button	= (lang  (concat (concat (concat (concat 'wbBanner.' ../prefix) '.banner') $index)'.button'))
			}}
			{{#and (if ../display_mode '==' 'grid121') (if $index '==' 3)}}</div> {{/and }}

		{{/for}}
	{{else}}
		{{> components/wbcomponents/addons/wb_banner/default/banner-item
			show_content = showContent
			image	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.image'))
			placeholder	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.placeholderImage'))
			icon	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.icon'))
			url		= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.url'))
			title	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.title'))
			text	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.text'))
			button	= (lang    (concat (concat 'wbBanner.' prefix) '.banner1.button'))
		}}
	{{/if}}

	</div>
</div>
{{/if}}
