components/wbcomponents/addons/wb_latest:
{{#if status }}
<div class="module-wrapper wb-latestblog {{class_sfx}} " >
	{{#unless hide_heading}}
		<h3 class="block-title">
			<span class="title">{{lang 'wbTheme.latest_blog.title' }}</span>
			<span class="subtitle">{{lang 'wbTheme.latest_blog.subtitle' }}</span>
		</h3>

	{{/unless}}
    <div class="block-content">
    	<article class="latest-blog--carousel productCarousel"
    		data-slick='{
				"dots": false,
				"arrows": true,
		        "infinite": false,
		        "mobileFirst": true,
		        "slidesToShow": 1,
		        "slidesToScroll": 1,
		        "responsive": [
					 {
						"breakpoint": 1900,
						"settings": {
							"slidesToScroll": 1,
							"slidesToShow": {{column}}
						}
					},
		            {
		                "breakpoint": 1200,
		                "settings": {
		                    "slidesToScroll": 1,
		                    "slidesToShow": {{subtract column  1}}
		                }
		            },
					{
		                "breakpoint": 992,
		                "settings": {
		                    "slidesToScroll": 1,
		                    "slidesToShow": 3
		                }
		            },
		            {
		                "breakpoint": 700,
		                "settings": {
		                    "slidesToScroll": 1,
							"slidesToShow": 3
		                }
		            },
		            {
		                "breakpoint": 0,
		                "settings": {
		                    "slidesToScroll": 1,
							"slidesToShow": 1,
							"dots": true,
							"arrows": false
		                }
		            }
		        ]
		    }'
    	>

    		{{#each blogs}}
		    <div class="productCarousel-slide">
		        {{> components/wbcomponents/addons/wb_latest_blog/item post=this show_desc=../show_desc settings=../settings hide_readmore=../hide_readmore}}

		    </div>
		    {{/each}}


		</article>
    </div>

</div>
{{/if}}
